        /* Css Reset */
        *{margin:0;padding:0;-webkit-box-sizing:border-box;-moz-box-sizing:border-box;box-sizing:border-box;word-wrap:break-word;line-height:140%;}
        *:before,*:after{-webkit-box-sizing: border-box;-moz-box-sizing: border-box;box-sizing: border-box;}
        a{text-decoration: none;outline: none;color:inherit;}
        a:hover{cursor: pointer;}
        a:focus {outline:none;box-shadow: none;}
        img {outline:none;border: 0;vertical-align: middle;}
        ul{list-style: none}
        b,strong {font-weight: 700;}
        h1,h2,h3,h4,h5,h6{font-weight: 400;}
        footer,header,nav{display: block;}
        html {-ms-text-size-adjust: 100%;-webkit-text-size-adjust: 100%;-ms-text-size-adjust:100%;}
        .container,.container-fluid{margin-right:auto;margin-left:auto;padding-left:14px;padding-right:14px}
        .slick-slide{outline: none;}
        .img-responsive {display: block;width: 100%;height: auto;}
        .hidden{opacity: 0;}
        .visible{opacity: 1;}
        .tran{ transition: all 0.30s ease-in-out;-webkit-transition: all 0.30s ease-in-out;-moz-transition: all 0.30s ease-in-out;}

        .clearfix:after,.clearfix:before,.container-fluid:after,.container-fluid:before,.container:after,.container:before,.row:after,.row:before{content:" ";display:table}
        .clearfix:after,.container-fluid:after,.container:after,.row:after{clear:both}
        @media (min-width:768px){.container{max-width:870px}}

        body{font-size: 16px;color: #676666;font-family: 'Open Sans', sans-serif;font-weight: 400;}

        /* Styl for Header */
        .header{position: absolute;min-height: 88px;z-index: 3;left: 106px;right: 14px;}
        .logo{float: left;Width:215px;padding-top: 43px;}
        .logo a{position: relative;display: block;width: 100%;height: 100%;}
        .logo img{max-width: 100%;height: auto;display: block;vertical-align: middle;}

        .fl-banner-sec{position: relative;background-color: #191919;}
        .fl-banner-sec:before{position: absolute;content:'';left:0;right:0;bottom:0;width: 100%;height:248px;background: url(assets/img/bg-with-bottom-patch.png) no-repeat center center;background-size:calc(100%*16/16) 100%;z-index: 2;}
        .fl-inn-banner-wrap{position: relative;width: 100%;height: 100%;padding: 0 0 0 106px;}
        .fl-banner-left{width: 33%;position: absolute;top: 50%;transform: translateY(-50%);z-index: 4;}
        .fl-banner-right{float: right;width: 848px;position: relative;}
        .banner-overlay{position: relative;}
        .banner-overlay #whyalone{display: block;color:#FFCB27;overflow: hidden;}
        .banner-overlay h2{display: block;color:#FFFFFF;font-size: 32px;overflow: hidden;font-family: 'Fredoka One';margin:0 0 11px 0;}
        .banner-overlay p{display: block;color:#979797;overflow: hidden;margin:0 0 20px 0;}
        .btn{position: relative;border-radius: 5px;background-color: #DE122E;padding: 20px 44px;color: #FFFFFF;font-size: 13px;font-weight: 700;text-transform: uppercase;}
        .download-btn{display: inline-block;}
        .btn:hover{background-color:#cd0f2a;}
        /* .mob-img{display: none;} */

        /* Stype for Product Features */
        .product-feat-sec{position: relative;padding: 110px 106px 56px 106px;text-align: center;}
        .heading{position: relative;margin:0 0 44px 0;}
        .heading h2{color: #333333;font-size: 32px;overflow: hidden;font-family: 'Fredoka One';}
        .heading p{overflow: hidden;}
        .ft-row{margin:0 -14px;}
        .feat-grid{float: left;width: 33.33%;padding: 0 14px;}
        .product-feat-item{max-width: 300px;margin:0 auto;}
        .product-feat-item .imgbox{position: relative;margin:0 0 14px 0;}
        .product-feat-item .prod-text-content{position: relative;}
        .product-feat-item .prod-text-content h3{color: #333333;font-size: 18px;font-weight: 700;overflow: hidden;margin:0 0 6px 0;}
        .product-feat-item .prod-text-content p{overflow: hidden;}

        /* Stype for Product Features */
        .pause-talk-sec{position: relative;background-color:#e0e0e0;background: linear-gradient(180deg, rgba(224,224,224,0.15) 0%, rgba(255,255,255,0.05) 100%, rgba(255,255,255,0) 100%);padding: 50px 0;text-align: center;}
        .pause-talk-block{position: relative;}
        .pause-talk-block .imgbox{margin:0 0 30px 0;}

        /* Stype for Supported Platform */
        .supp-plat-sec{position: relative;background-color:#F6F6F6;padding: 64px 0 0 0;text-align: center;margin-bottom:100px;}
        .supp-plat-sec:before{position: absolute;content:'';left:0;bottom:-94px;background: url(assets/img/support-bg.png) no-repeat center center;width: 100%;height: 277px;background-size:calc(100%*16/16) 100%;}
        .supp-plat-sec .heading{margin-bottom: 30px;}
        .supp-plat-wrap{position: relative;}
        .netflix-logo{position: absolute;left: 50%;top: 0;transform: translateX(-50%);z-index: 1;}
        .supp-plat-slider{position: relative;}
        .slick-dots{width: auto;left: 50%;transform: translateX(-50%);bottom: -16px;}
        .slick-dots li {width: 23px;height: 6px;border-radius: 10px;}
        .slick-dots li button{width: 23px;height: 6px;border-radius: 10px;background-color: #D3D3D3;padding: 0;}
        .slick-dots li button:before{display: none;}
        .slick-dots li.slick-active button{background-color: #DE122E;box-shadow: 0 2px 4px 0 #DE122E;}
        .slick-dotted.slick-slider{margin:0;}

        .supp-plat-slider{opacity: 0;visibility: hidden;transition: opacity 1s ease;-webkit-transition: opacity 1s ease;}
        .supp-plat-slider.slick-initialized{visibility: visible;opacity: 1;  }

        /* Stype for Privacy Friendly */
        .privacy-frd-sec{position: relative;padding: 40px 0 70px 0;text-align: center;}
        .privacy-frd-block{position: relative;overflow: hidden;padding: 20px 0;}
        .privacy-frd-block:before{position: absolute;content:'';left:50%;top:0;bottom:0;width: 15px;background: url(data:image/svg+xml;base64,);background: -moz-linear-gradient(left,  rgba(0,0,0,0.15) 0%, rgba(0,0,0,0) 100%);
            background: -webkit-linear-gradient(left,  rgba(0,0,0,0.15) 0%,rgba(0,0,0,0) 100%);background: linear-gradient(to right,  rgba(0,0,0,0.15) 0%,rgba(0,0,0,0) 100%);transform: rotate(22deg);}
        .privacy-frd-block:after{position: absolute;content:'Vs.';left:50%;top:30px;width: 44px;height: 44px;line-height: 44px;margin-left:30px;background-color: #D5D5D5;border-radius: 50%;color: #333333;font-size: 18px;font-weight: 700;}
        .privacy-frd-left{float: left;width: 40%;}
        .privacy-frd-right{float: right;width: 40%;}
        .privacy-frd-block span{display: block;color: #333333;font-size: 18px;font-weight: 700;overflow: hidden;margin:0 0 20px 0;}
        .privacy-frd-block img{vertical-align: bottom;display:block;}
        .privacy-frd-note{position: relative;margin-top:24px;border-radius: 3px;background-color: #F2F3F6;padding: 3px 24px;}
        .privacy-frd-note p{font-size: 13px;overflow: hidden;}


        /* Stype for Why Watch Section */
        .why-watch-sec{position:relative;background-color: #171717;padding: 30px 0;text-align: center;}
        .why-watch-wrap{position: relative;width: 100%;max-width: 450px;margin: 0 auto;}
        .why-watch-wrap p{color: #FFCB27;font-weight: 600;overflow: hidden;margin:0 0 22px 0;line-height: 24px;}
        .why-watch-wrap h2{color: #FFFFFF;font-size: 32px;font-family: 'Fredoka One';overflow: hidden;margin:0 0 26px 0;}

        /* Stype for Footer */
        .footer{position: relative;background-color: #222222;padding: 16px 0;text-align: center;}
        .footer-wrap{position: relative;font-size: 0;}
        .footer-logo{display: inline-block;width: 140px;margin-right:12px;}
        .footer-logo a{position: relative;display:block;}
        .footer-links{display: inline-block;}
        .footer-links ul li{width: auto;position: relative;display: inline-block;margin:0 10px;}
        .footer-links ul li:before{position: absolute;content:'';right:-11px;top:50%;transform: translateY(-50%);width: 1px;height: 13px;background-color: #FFFFFF;}
        .footer-links ul li:last-child:before{display:none;}
        .footer-links ul li a{position: relative;display: block;font-size: 13px;color: #FFFFFF;overflow: hidden;line-height: 26px;}
        .footer-links ul li a:hover{color:#FFCB27;}

        @media screen and (min-width: 1900px){
            body, .privacy-frd-block span{font-size: 22px}
            .footer-links ul li a{font-size: 16px}
            .heading h2, .banner-overlay h2{font-size: 40px}
            .why-watch-wrap{max-width: 650px}
        }
        @media screen and (min-width: 1600px){
            .header{left: 50%;right: 0;width: 1400px;margin-left: -700px}
            .fl-inn-banner-wrap, .product-feat-block {padding: 0 0;max-width: 1400px;margin: 0 auto}

        }
        @media screen and (max-width: 1500px){
            .fl-banner-right {width: 780px;}
        }
        @media screen and (max-width: 1280px){
            .fl-banner-right {width: 710px;}
        }
        @media screen and (max-width: 1200px){
            .header {min-height: 70px;left: 0;right: 0;padding: 0 14px;}
            .fl-inn-banner-wrap {padding: 0 0 0 14px;}
            .product-feat-sec {padding: 80px 14px 40px 14px;}
            .supp-plat-sec {padding: 40px 0 0 0;margin-bottom: 60px;}
            .supp-plat-sec:before {bottom: -50px;height: 200px;}
            .why-watch-sec {padding: 30px 14px;}
            .footer {padding: 16px 14px;}
            .fl-banner-sec:before {bottom:-1px;height: 150px;}
            .logo {padding-top: 14px;}
            .fl-banner-right {width: 620px;}
            .banner-overlay h2 {font-size: 26px;}
        }
        @media screen and (max-width: 1024px){

            .pause-talk-sec{padding: 30px 0;}
            .privacy-frd-sec {padding: 30px 0 40px 0;}
            .privacy-frd-note {padding: 3px 10px;}
            .fl-banner-right {width: 544px;}
        }
        @media screen and (max-width: 899px){
            .fl-inn-banner-wrap {padding: 70px 0 100px 0;min-height: auto;}
            .fl-banner-right {float: left;width: 100%;margin-bottom: 20px;}
            .fl-banner-left {width: 100%;position: relative;top: auto;transform: none;float: left;margin: 0 0 20px 0;padding: 0 14px;}
            .supp-plat-sec:before {bottom: -46px;height: 80px;}
            /* .desk-img{display: none;}
            .mob-img{display: block;} */
        }
        @media screen and (max-width: 767px){
            .banner-overlay h2 {font-size: 24px;margin: 0 0 6px 0;}
            .banner-overlay span{font-size: 14px;}
            .banner-overlay p {margin: 0 0 14px 0;font-size: 14px;}
            .feat-grid {width: 100%;}
            .product-feat-item {max-width: 100%;margin: 0 auto 20px;}
            .product-feat-item .imgbox {margin: 0 auto 14px;max-width: 260px;}
            .product-feat-item .prod-text-content h3 {font-size: 16px;}
            .product-feat-item .prod-text-content p, .heading p {font-size: 14px;}
            .heading h2 {font-size: 24px;}
            .supp-plat-slider {padding: 10px 0 0 0;}
            .privacy-frd-left {width: 100%;margin:0 0 88px 0;}
            .privacy-frd-right {float:left;width: 100%;}
            .privacy-frd-block .imgbox{max-width: 400px;margin: 0 auto;}
            .privacy-frd-block:before {left:0;top: 50%;bottom: auto;width: 100%;height: 15px;background: url(data:image/svg+xml;base64,);
                background: -moz-linear-gradient(top, rgba(0,0,0,0.15) 0%, rgba(0,0,0,0) 100%);background: -webkit-linear-gradient(top, rgba(0,0,0,0.15) 0%,rgba(0,0,0,0) 100%);
                background: linear-gradient(to bottom, rgba(0,0,0,0.15) 0%,rgba(0,0,0,0) 100%);margin-top: -10px;transform: none;}
            .privacy-frd-block:after {left: 50%;top: 50%;transform: translateX(-50%);margin-top: -25px;margin-left: 0;}
            .privacy-frd-block span {font-size: 16px;}
            .why-watch-wrap h2 {font-size: 24px;margin: 0 0 18px 0;}
            .why-watch-wrap p {margin: 0 0 14px 0;line-height: 20px;font-size: 14px;}
        }
        .supp-plat{
            margin-top:20px;
        }

        .supp-plat-logo {
            width: 55px;
            border-radius: 5px;
            margin-right: 20px;
        }

        /* The Overlay (background) */
        .overlay {
            /* Height & width depends on how you want to reveal the overlay (see JS below) */
            height: 0;
            width: 100%;
            position: fixed; /* Stay in place */
            z-index: 10; /* Sit on top */
            left: 0;
            top: 0;
            background-color: rgb(0,0,0); /* Black fallback color */
            background-color: rgba(0,0,0, 0.75); /* Black w/opacity */
            overflow-x: hidden; /* Disable horizontal scroll */
            overflow-y: hidden; /* Disable horizontal scroll */
            transition: 0.5s; /* 0.5 second transition effect to slide in or slide down the overlay (height or width, depending on reveal) */
        }

        /* Position the content inside the overlay */
        .overlay-content {
            position: relative;
            top: 10%; /* 25% from the top */
            width: 1120px; /* 100% width */
            height: 630px;
            margin: auto;
            text-align: center; /* Centered text/links */
            margin-top: 30px; /* 30px top margin to avoid conflict with the close button on smaller screens */
        }

        /* The navigation links inside the overlay */
        .overlay a {
            padding: 8px;
            text-decoration: none;
            font-size: 36px;
            color: #818181;
            display: block; /* Display block instead of inline */
            transition: 0.3s; /* Transition effects on hover (color) */
        }

        /* When you mouse over the navigation links, change their color */
        .overlay a:hover, .overlay a:focus {
            color: #f1f1f1;
        }

        /* Position the close button (top right corner) */
        .overlay .closebtn {
            position: absolute;
            top: 20px;
            right: 45px;
            font-size: 60px;
        }
        .demo-link{
            padding: 20px;
            margin-left: 60px;
            color: white;
        }
        /* When the height of the screen is less than 450 pixels, change the font-size of the links and position the close button again, so they don't overlap */
        @media screen and (max-height: 450px) {
            .overlay a {font-size: 20px}
            .overlay .closebtn {
                font-size: 40px;
                top: 15px;
                right: 35px;
            }

        }
        @media screen and (max-width: 900px){
            .overlay-content{
                width: 560px;
                height:314px
            }

        }
        @media screen and (max-width: 599px){
            .overlay-content{
                width: 280px;
                height:157px
            }

        }

        @media screen and (max-width: 380px){
            .supp-plat-slider {padding: 15px 0 0 0;}
        }

    </style>
