<script src="assets\js\lib.min.js?v=1"></script>
<script>
    var $ = jQuery.noConflict();
    $(document).ready(function () {
        $('.supp-plat-slider').slick({
            slidesToShow: 1,
            slidesToScroll: 1,
            autoplay: true,
            autoplaySpeed: 3000,
            dots: true,
            arrows: false
        });
        $('body').on('click', '.overlay', function (e) {
            closeNav();
            closeMobileMsg();
            e.preventDefault();
        });

    });

    function openNav() {
        $('#myNav iframe').attr('src', 'https://www.youtube.com/embed/mW0LzMpyHjw?autoplay=1');
        document.getElementById("myNav").style.height = "100%";
        return false;
    }

    /* Close when someone clicks on the "x" symbol inside the overlay */
    function closeNav() {
        $('#myNav iframe').attr('src', '');
        document.getElementById("myNav").style.height = "0";
    }
    function openMobileMsg() {
        document.getElementById("visitOnDesktop").style.height = "100%";
        return false;
    }

    /* Close when someone clicks on the "x" symbol inside the overlay */
    function closeMobileMsg() {
        document.getElementById("visitOnDesktop").style.height = "0";
    }
    function fbTrack(event, customObj) {
        if(customObj === undefined)
            customObj = {};
        if(typeof  fbq === "function")
            fbq('track', event,customObj);
    }

    function optimizeDesktop() {
        const parser = bowser.getParser(window.navigator.userAgent);
        if(parser.is("desktop") && parser.is("chrome")) {
            setTimeout(function () {
                sendEvent("optimize_desktop");
                fbTrack('Purchase', {value: .4, currency: 'inr'});
            }, 15000);
        }
    }
    //optimizeDesktop();
    $('body').on('click', '.install-cta', function (e) {
        try {
            const parser = bowser.getParser(window.navigator.userAgent);
            const platformType = parser.parsedResult.platform.type;
            fbTrack('InitiateCheckout');
            if (platformType !== "desktop") {
                openMobileMsg();
                sendEvent('install_cta_mobile');
                return false;
            }
            sendEvent('install_cta');
        }catch (e){
            console.log(e);
        }
    });
    $('body').on('click', '.demo-cta', function (e) {
        sendEvent('demo_cta');
    });

    function sendEvent(action, data) {
        if (typeof gtag === "function") {
            gtag('event', action, data);
        }
    }

    function setCookie(name, value, days) {
        var expires = "";
        if (days) {
            var date = new Date();
            date.setTime(date.getTime() + (days * 24 * 60 * 60 * 1000));
            expires = "; expires=" + date.toUTCString();
        }
        document.cookie = name + "=" + (value || "") + expires + "; path=/";
    }

    function setDistributionParams() {
        const dayExpiry = 7;
        const url = new URL(window.location.href);
        const source = url.searchParams.get("utm_source");
        const medium = url.searchParams.get("utm_medium");
        const campaign = url.searchParams.get("utm_campaign");
        const term = url.searchParams.get("utm_term");
        let referrerUrl = document.referrer;
        let referrerDomain = "";
        if (referrerUrl) {
            const refUrl = new URL(referrerUrl);
            referrerDomain = refUrl.hostname;
        }else{
            referrerUrl = "direct_lander";
        }
        if(campaign && campaign.toLowerCase().includes("ipl")){
            $('#h_pitch').text("Watch IPL live with your gang from comfort of your home!")
        }
        if(!!source)
            setCookie("us", source, dayExpiry);
        if(!!medium)
            setCookie("um", medium, dayExpiry);
        if(!!campaign)
            setCookie("uc", campaign, dayExpiry);
        if(!!term)
            setCookie("ut", term, dayExpiry);
        if(!!referrerUrl)
            setCookie("ru", referrerUrl, dayExpiry);
        if(!!referrerDomain)
            setCookie("rd", referrerDomain, dayExpiry);
    }
    setDistributionParams();
</script>
</body>
